import { useState } from 'react';
import styles from 'D:/project_testpaje/animals/src/Slider/Slider_style.css';

const slides = [
    { id: 1, image: 'slide1.jpg', alt: '����������� 1' },
    { id: 2, image: 'slide2.png', alt: '����������� 2' },
    { id: 3, image: '/slide3.png', alt: '����������� 3' },
];

export default function Slider() {
    const [currentSlide, setCurrentSlide] = useState(0);

    const nextSlide = () => {
        setCurrentSlide((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
    };

    return (
        <div className={styles.sliderContainer}>
            <div className={styles.slider}>
                {slides.map((slide, index) => (
                    <img
                        key={slide.id}
                        src={slide.image}
                        alt={slide.alt}
                        className={styles.slide}
                        style={{ display: index === currentSlide ? 'block' : 'none' }}
                    />
                ))}
                <button onClick={prevSlide} className={styles.arrow}>�</button>
                <button onClick={nextSlide} className={styles.arrow}>�</button>
                <div className={styles.dots}>
                    {slides.map((_, index) => (
                        <button
                            key={index}
                            className={`${styles.dot} ${index === currentSlide ? styles.active : ''}`}
                            onClick={() => setCurrentSlide(index)}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
}
