import React from 'react';


let counter = 0;
function nextSlide() {
    counter++;
    console.log(counter);
    switch (counter) {
        case 1:
            document.getElementsByClassName('firstImage')[0].style.display = 'none';
            document.getElementsByClassName('secondImage')[0].style.display = 'flex';
            break;
        case 2:
            document.getElementsByClassName('firstImage')[0].style.display = 'none';
            document.getElementsByClassName('secondImage')[0].style.display = 'none';
            document.getElementsByClassName('thirdImage')[0].style.display = 'flex';
            break;
        default:
            alert("You've reached maximum");
            counter--;
    }
}

function prevSlide() {
    counter--;
    console.log(counter);
    switch (counter) {
        case 0:
            document.getElementsByClassName('firstImage')[0].style.display = 'flex';
            document.getElementsByClassName('secondImage')[0].style.display = 'none';
            document.getElementsByClassName('thirdImage')[0].style.display = 'none';
            break;
        case 1:
            document.getElementsByClassName('firstImage')[0].style.display = 'none';
            document.getElementsByClassName('secondImage')[0].style.display = 'flex';
            document.getElementsByClassName('thirdImage')[0].style.display = 'none';
            break;
        case 2:
            document.getElementsByClassName('firstImage')[0].style.display = 'none';
            document.getElementsByClassName('secondImage')[0].style.display = 'none';
            document.getElementsByClassName('thirdImage')[0].style.display = 'flex';
            break;
        default:
            alert("You've reached minimum");
            counter++;
    }
}
