import { useState } from 'react';
import styles from 'D:/project_testpaje/animals/src/Slider/Slider_style.css';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

const slides = [
    { id: 1, image: 'slide1.jpg', alt: '�������� 1' },
    { id: 2, image: 'slide2.jpg', alt: '�������� 2' },
    { id: 3, image: 'slide3.jpg', alt: '�������� 3' },
];

export default function Slider() {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [direction, setDirection] = useState('right');

    const goToSlide = (index) => {
        setDirection(index > currentSlide ? 'right' : 'left');
        setCurrentSlide(index);
    };

    const nextSlide = () => {
        setDirection('right');
        setCurrentSlide((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
    };

    const prevSlide = () => {
        setDirection('left');
        setCurrentSlide((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
    };

    return (
        <div className={styles.slider}>
            <TransitionGroup>
                <CSSTransition
                    key={currentSlide}
                    timeout={500}
                    classNames="fade"
                >
                    <div className={styles.slideContainer}>
                        <img
                            src={slides[currentSlide].image}
                            alt={slides[currentSlide].alt}
                            className={styles.slide}
                            onError={(e) => {
                                e.target.src = '/assets/images/placeholder.jpg'; // �������� �����������
                            }}
                        />
                    </div>
                </CSSTransition>
            </TransitionGroup>

            <button onClick={prevSlide} className={styles.arrow} aria-label="Previous slide">�</button>
            <button onClick={nextSlide} className={styles.arrow} aria-label="Next slide">�</button>

            <div className={styles.dots}>
                {slides.map((_, index) => (
                    <button
                        key={index}
                        className={`${styles.dot} ${index === currentSlide ? styles.active : ''}`}
                        onClick={() => goToSlide(index)}
                        aria-label={`Go to slide ${index + 1}`}
                    />
                ))}
            </div>
        </div>
    );
}
